    public static Cave buildCave(String input) {
        List<List<int[]>> formations = processInput(input);
        // Processing the limits of the cave
        int minX = 600, maxX = 400, height = 0;
        for (List<int[]> formation : formations) {
            for (int[] point : formation) {
                minX = Math.min(minX, point[0]);
                maxX = Math.max(maxX, point[0]);
                height = Math.max(height, point[1]);
            }
        }
        // Creating the cave
        Cave cave = new Cave();
        cave.startX = minX;
        cave.endX = maxX;
        cave.drop = 500 - cave.startX;
        cave.map = new char[height + 1][cave.endX - cave.startX + 1];
        // Fill the map
        for (int i = 0; i < cave.map.length; i++) {
            for (int j = 0; j < cave.map[0].length; j++) cave.map[i][j] = '-';
        }
        // Populating the cave
        ListIterator<int[]> iter;
        int[] prev, curr;
        int delta;
        for (List<int[]> formation : formations) {
            iter = formation.listIterator();
            // Will always have a next value at the start
            prev = iter.next();
            while (iter.hasNext()) {
                curr = iter.next();
                // Determine which axis repeats itself
                // Along the X axis
                if (curr[0] == prev[0]) {
                    if (curr[1] > prev[1]) {
                        for (int i = prev[1]; i <= curr[1]; i++) cave.map[i][curr[0] - minX] = '#';
                    } else {
                        for (int i = prev[1]; i >= curr[1]; i--) cave.map[i][curr[0] - minX] = '#';
                    }
                }
                // Along the Y axis
                else {
                    if (curr[0] > prev[0]) {
                        for (int i = prev[0]; i <= curr[0]; i++) cave.map[curr[1]][i - minX] = '#';
                    } else {
                        for (int i = prev[0]; i >= curr[0]; i--) cave.map[curr[1]][i - minX] = '#';
                    }
                }
                prev = curr;
            }
        }
        return cave;
    }

    public static List<List<int[]>> processInput(String input) {
        String[] formations = input.split("\n"), parts, coords;
        List<List<int[]>> res = new ArrayList<>();
        List<int[]> tmp;
        for (String formation : formations) {
            parts = formation.split(" -> ");
            tmp = new ArrayList<>();
            for (String coord : parts) {
                coords = coord.split(",");
                tmp.add(new int[]{Integer.parseInt(coords[0]), Integer.parseInt(coords[1])});
            }
            res.add(tmp);
        }
        return res;
    }

    public static class Cave {
        public char[][] map;
        public int startX, endX, drop;
    }
